package com;

public class SwitchingStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char grade = 'k';
		switch(grade)
		{
		case'A':System.out.println("excelent FCD");
		break;
		case 'B':System.out.println("FCD");
		break;
		case 'C':System.out.println("FAIL");
		break;
		default:System.out.println("INVALID input");
		}

	}

}
